mapper.select:
select ai.*
 from appinfo ai
 where appId=#{appId}

select * from appinfo
and instr(appId, #{appId}) = 1
order by id desc limit #{offset}, #{limit}

select * 
 from appversion


 		appId=#{appId}


 order by id desc
 limit #{offset}, #{limit}

select *
  from appversion
  where appId=#{appId} and version=#{version}

mapper.update:
update appaction set
 state=#{state},
 result=#{result}
 where id=#{id}

mapper.select:
select *
 from appaction


 		appId=#{appId}


 order by id desc
 limit #{offset}, #{limit}
